import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import JWTDecode from 'jwt-decode';
import axios from "axios";

const API = axios.create({
   baseURL:`${process.env.REACT_APP_BASE_API_URL}/auth`
});

const initialState = {
    token:null,
    user:null, 
}

export const loginHandler = createAsyncThunk('auth/login' , async ({ loginForm,setlogin,dispatch }) => {
    try {
 

        const { data } = await API.post('/login' , loginForm);
        if(data) {
            return data;
        }

    } catch(err) {
        return null;
    }
});

export const registerHandler = createAsyncThunk('auth/register' , async ({ registerForm }) => {
     try {
        
        const { data } = await API.post('/login' , registerForm);
        if(data) {
            return data;
        }

     }catch(err) {
        return null;
     }
});

const AuthSlice = createSlice({
    name:'auth', 
    initialState,
    reducers: {

    },
    extraReducers:(builder)=> {
       builder.addCase(loginHandler.fulfilled, (state, { payload }) => {
          state.token = payload;
          state.user = payload ? JWTDecode(payload) : null;

          localStorage.setItem('token' , JSON.stringify(state.token));
          return state;
       });

       builder.addCase(registerHandler.fulfilled, (state,  { payload }) => {
        state.token = payload;
        state.user = payload ? JWTDecode(payload) : null;

        localStorage.setItem('token' , JSON.stringify(state.token));
        return state;
       });
    }
});

export default AuthSlice.reducer;
