import { createSlice,createAsyncThunk } from '@reduxjs/toolkit';
import axios, { formToJSON } from 'axios';
import { Form } from 'react-router-dom';

const user = JSON.parse(localStorage.getItem('token'));

const API = axios.create({
    baseURL:`${process.env.REACT_APP_BASE_API_URL}/posts`
});

API.interceptors.request.use(request => {
    if(user){
        request.headers.Authorization = `bearer ${user}`;
    }

    return request;
});

export const getAllPosts = createAsyncThunk('post/get' , async () => {
     try {
        const { data } = await API.get('/all');
        if(data) {
            return data;
        }

     } catch(err) {
        return null;
     }
});

export const createPost = createAsyncThunk('post/create' , async ({ postForm }) => {
    try {
        const formData = new FormData();
        formData.append('title' , postForm.title);
        formData.append('description',postForm.description);
        formData.append('image' , postForm.image);

        const { data } = await API.post('/create' , formData);

        if(data) {
            return data;
        }

    } catch(err) {
        return null;
    }
});

const PostSlice = createSlice({
    name:'post',
    initialState: {
        posts:[],
        loading:false 
    },
    extraReducers:(builder) => {

    }
});

export default PostSlice.reducer;
