import { Navbar } from "../components";
import { useSelector } from "react-redux";
import { useState } from "react";

const Upload = () => {
    const [postForm,setPostForm] = useState(new FormData());
    const { auth:{ user , token } } = useSelector(state=>state);

    const changeHandler = (e) => {
        postForm.append(e.target.name , e.target.value);
    }

    const imageHandler = (element) => {
        const file = element.target.files[0];
        const reader = new FileReader();
        reader.onloadend = function() {
            postForm.append('image' , file);
        }
        reader.readAsDataURL(file);
    }

    const submitHandler = (e) => {
        e.preventDefault();
    }
 
    return (
        <div className="w-full bg-gray-100 min-h-screen">
            <Navbar/>
            <div className="w-[50vw] py-5 mt-10 px-5 mx-auto bg-white rounded-lg flex justify-between items-start">
                <input onChange={imageHandler} type="file" className="hidden" id="image" name="image"/>
                <label for="image" className="bg-gray-100 rounded-lg w-[48%] h-[330px] flex justify-center items-center">
                <i className="ri-image-add-line text-5xl text-gray-500"></i>
                </label>
                <form className="w-[48%]">
                    <div className="flex items-start gap-x-3">
                    <span className={`w-[46px] text-white font-bold h-[46px] rounded-full bg-pink-500 flex items-center justify-center uppercase`}>
                    {user?.username?.charAt(0)}
                   </span>
                   <div className="flex flex-col">
                    <h5 className="text-[15px] font-bold">{user?.display_name}</h5>
                    <p className="text-[12px] font-medium text-gray-500">{user?.username}</p>
                   </div>
                    </div>
                    <div className="flex mt-5 flex-col gap-y-2">
                        <label className="font-semibold text-[15px]">Title</label>
                        <input value={postForm.title} name='title' type="text" className="w-full bg-gray-100 rounded-md outline-none py-2 px-3"/>
                    </div>
                    <div className="flex mt-3 flex-col gap-y-2">
                        <label className="font-semibold text-[15px]">Description</label>
                        <textarea value={postForm.description} name='description' type="text" className="w-full bg-gray-100 rounded-md outline-none py-2 px-3"></textarea>
                    </div>
                    <div className="flex justify-end items-center text-white mt-10 gap-x-3">
                        <button className="bg-pink-500 text-[15px] font-semibold py-1 px-3 rounded-md">Save</button>
                        <button className="text-pink-500 border border-pink-500 text-[15px] font-semibold py-1 px-3 rounded-md">Cancel</button>

                    </div>
                </form>
            </div>
        </div>
    )
}

export default Upload;
